class DPAdsPlugin extends DPBase{constructor(app){super();this.config=app.config;this.app=app;this.helper=app.helper;this.currentSetting={};this.usedType=[]}
init(){let icon=this.config.get('icons.close');let close=this.config.get('elements.adsClose',!0);let ads=this.config.get('elements.ads');let instance=this;let runner=this.config.runner(!0).node();this.isPlay=!runner.paused;this.runningAds=null;close.listen('click',function(){window.clearTimeout(this.runningAds);__dp.node(this).closest(ads).removeClass('active');if(instance.currentSetting.type==='full'&&instance.isPlay){instance.app.source.play()}});close.html(icon);function __callResize(){instance.resize()}
__dp.node(window).listen('resize',__callResize);this.app.event.listen('dp.control.hide',__callResize).listen('dp.control.show',__callResize);return this}
resize(){if(this.currentSetting.type==='line'){let control=this.config.get('elements.control',!0);let ads=this.config.get('elements.ads',!0);if(control.hasClass('active')){let height=control.height();ads.css('bottom',(height+1)+'px')}else{ads.css('bottom','10px')}}}
show(content,conf){let ads=this.config.get('elements.ads',!0);let adsClose=this.config.get('elements.adsClose',!0);let adsContent=this.config.get('elements.adsContent',!0);let runner=this.config.runner(!0).node();let instance=this;this.isPlay=!runner.paused;if(conf!==undefined){this.currentSetting=this.or(conf,{})}
this.currentSetting.type=this.or(this.currentSetting.type,'line');ads.removeClass(this.usedType.join(' ')).addClass(this.currentSetting.type);ads.addClass('active');adsClose.addClass('active');this.usedType.push(this.currentSetting.type);if(content!==undefined){adsContent.html(content)}
if(this.currentSetting.type==='full'){this.app.source.pause();this.runningAds=window.setTimeout(function(){ads.removeClass('active');if(instance.isPlay){instance.app.source.play()}},this.currentSetting.time)}
this.resize();return this}}